function appendArticles(e,t){var a=e.selectAll("div.article").data(graph.nodes.filter(function(e){return e.value.distance==t})).enter().append("div").attrs({class:"article card",id:function(e){return"a_"+e.id}}).text(function(e){return e.value.title});a.append("p").classed("articleTitle",!0).text(function(e){return e.value.title}).append("span").classed("rank",!0).text(function(e){return e.value.rank});var n=a.filter(function(e){return e.value.hasOwnProperty("image")&&null!=e.value.image}).append("div").classed("imgBox",!0).append("img").attrs({src:function(e){return e.value.image},class:"svgHoverImg"})}